    public string Region { get; set; }


    //Regional GETS

    private async Task GetDataAsync()
    {
        try
        {
            string uri = "https://restcountries.eu/rest/v2/region/" + Region;
            data = await Http.GetFromJsonAsync<List<CountryData>>(uri);
            errorMsg = String.Empty;
